package module1;

public class Pattern6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int rows=10;
		int number=0;
		for(int i=1;i<=rows;i++) {
			for(int j=1;j<=i;j++) {
				System.out.print(number + " ");
			}
			System.out.println();
			number++;
		}
			

	}

}
