package module1;

public class Pattern7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=6;
		for(int i=1; i<=n; i++) {
			for(int j=i; j<n; j++) {
				System.out.println(" ");
				}
			for(int k=1;  k<=i;k++) {
				System.out.print(i+" ");
				
			}
			System.out.println();
		}

	}

}
